import React, { useState } from "react";
const App = () => {
  const [temperature, setTemperature] = useState(25)
  const temperatureIncrease = () => {
    setTemperature(temperature + 1)
  }
  const temperatureDecrease = () => {
    setTemperature(temperature - 1)
  }
  return (
    <>
      <div className="flex justify-center items-center bg-gray-400 w-full h-screen">
        <div className="bg-blue-950 h-64 w-52 rounded-lg flex flex-wrap justify-center">
          <div className="bg-white h-40 w-40 my-4 rounded-full flex justify-center items-center">
            <div className="bg-blue-700 h-36 w-36   rounded-full flex flex-wrap justify-center items-center text-2xl">{temperature}&deg;C</div>
          </div>
          <div className="bg-transparent h-10 w-40 py-5 mb-3 flex justify-around items-center">
            <div className="h-10 w-10 bg-white flex justify-center items-center rounded-full">
              <button className="h-9 w-9  bg-slate-600 rounded-full  flex justify-center items-center text-3xl pb-2 text-white" onClick={temperatureIncrease}>+</button>
            </div>
            <div className="h-10 w-10 bg-white flex justify-center items-center rounded-full">
              <button className="h-9 w-9 bg-slate-600 rounded-full flex justify-center items-center text-3xl pb-2 text-white" onClick={temperatureDecrease}>-</button>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default App;
